            builder.Append("        <a onClick=\"history.back();return(false)\" class=\"btn cancel\" type=\"reset\">Cancel</a>\n");
            builder.Append("    </div>\n");
            builder.Append("    <fieldset>\n");
            builder.Append("<legend class=\"prtg-header\">Basic Notification Settings</legend><div class=\"control-group\">\n");
            builder.Append("<label class=\"control-label has_help \" for=\"name_\">Notification Name</label>\n");
            builder.Append("<div class=\"controls \" data-placement=\"right\" data-helptext=\"Enter a meaningful name for the notification.<p><a class='morehelp nohjax' target='_blank' href='/help/notifications_settings.htm#basic'>Further Help (Manual)</a></p>\">\n");
