            builder.Append("<label class=\"control-label has_help \" for=\"tags_\">Tags</label>\n");
            builder.Append("<div class=\"controls \" data-placement=\"right\" data-helptext=\"Enter a list of tags (not case sensitive) for filtering purposes (for example, the top 10 lists use these tags). Use spaces or commas as separators.<br/><br/> <b>Note:</b> Plus (+) or minus (-) are not allowed as first characters.<p><a class='morehelp nohjax' target='_blank' href='/help/notifications_settings.htm#basic'>Further Help (Manual)</a></p>\">\n");
            builder.Append($"<input class=\"text\"  type=\"text\" name=\"tags_\" id=\"tags_\" autocomplete=\"off\" value=\"{item?.Tags ?? "testTag1 testTag2"}\" ></div>\n");
            builder.Append("<label class=\"control-label has_help \" for=\"active_\">Status</label>\n");
            builder.Append("<div class=\"controls \" data-placement=\"right\" data-helptext=\"Use <b>Paused</b> to temporarily disable this notification. PRTG only sends messages when the status of the notification is <b>Started</b>.<p><a class='morehelp nohjax' target='_blank' href='/help/notifications_settings.htm#basic'>Further Help (Manual)</a></p>\">\n");
            builder.Append("<div class=\"radio-control\">\n");
            builder.Append("<input type=\"radio\" class=\"hidden radio \" name=\"active_\" value=\"1\" checked  id=\"active1\" >\n");
            builder.Append("<label for=\"active1\" class=\"radio-control-label\">\n");
            builder.Append("<i class=\"icon-gray icon-radio-on\"></i>\n");
            builder.Append("Started\n");
            builder.Append("</label>\n");
            builder.Append("<input type=\"radio\" class=\"hidden radio \" name=\"active_\" value=\"0\" id=\"active0\" >\n");
            builder.Append("<label for=\"active0\" class=\"radio-control-label\">\n");
            builder.Append("<i class=\"icon-gray icon-radio-on\"></i>\n");
            builder.Append("Paused\n");
            builder.Append("</label>\n");
            builder.Append("</div>\n");
            builder.Append("</div>\n");
            builder.Append("<label class=\"control-label has_help \" for=\"schedule_\">Schedule</label>\n");
            builder.Append("<div class=\"controls \" data-placement=\"right\" data-helptext=\"Use schedules to only activate notifications within specific time spans (days, hours) throughout the week. You can edit schedules in the account settings.<p><a class='morehelp nohjax' target='_blank' href='/help/notifications_settings.htm#basic'>Further Help (Manual)</a></p>\">\n");
