            builder.Append("<input type=\"radio\" class=\"hidden radio \" name=\"postpone_\" value=\"0\" id=\"postpone0\" >\n");
            builder.Append("<label for=\"postpone0\" class=\"radio-control-label\">\n");
            builder.Append("<i class=\"icon-gray icon-radio-on\"></i>\n");
            builder.Append("Discard notifications during paused status\n");
            builder.Append("</label>\n");
            builder.Append("<input type=\"radio\" class=\"hidden radio \" name=\"postpone_\" value=\"1\" checked  id=\"postpone1\" >\n");
            builder.Append("<label for=\"postpone1\" class=\"radio-control-label\">\n");
            builder.Append("<i class=\"icon-gray icon-radio-on\"></i>\n");
            builder.Append("Collect notifications and send them when reactivated\n");
            builder.Append("</label>\n");
            builder.Append("</div>\n");
            builder.Append("</div>\n");
            builder.Append("<span class=\" InitialDisplayNone \"><div class=\"wiz1-3\"><input type=\"hidden\" name=\"comments\" value=\"test\"></div></span>\n");
            builder.Append("</div></fieldset><fieldset>\n");
