            builder.Append("<label for=\"summode0\" class=\"radio-control-label\">\n");
            builder.Append("<i class=\"icon-gray icon-radio-on\"></i>\n");
            builder.Append("Always notify ASAP, never summarize\n");
            builder.Append("</label>\n");
            builder.Append("<input type=\"radio\" class=\"hidden radio  GroupShowHide\" name=\"summode_\" value=\"1\" id=\"summode1\" >\n");
