            builder.Append("Send all DOWN messages ASAP, summarize the others\n");
            builder.Append("</label>\n");
            builder.Append("<input type=\"radio\" class=\"hidden radio  GroupShowHide\" name=\"summode_\" value=\"4\" id=\"summode4\" >\n");
            builder.Append("<label for=\"summode4\" class=\"radio-control-label\">\n");
            builder.Append("<i class=\"icon-gray icon-radio-on\"></i>\n");
            builder.Append("Send all DOWN and UP messages ASAP, summarize the others\n");
            builder.Append("</label>\n");
