            builder.Append("<label class=\"control-label has_help  Showsummode1  Showsummode2  Showsummode3  Showsummode4  Showsummode5  Hidesummode0  groupshowhideelement\" for=\"summinutes_\">Timespan for Summarizing Messages (in Minutes)</label>\n");
