            builder.Append("<input class=\"text\"  data-rule-number=\"true\" type=\"text\" name=\"summinutes_\" id=\"summinutes_\" autocomplete=\"off\" value=\"1\" ></div>\n");
            builder.Append("</div></fieldset><fieldset>\n");
